import 'bootstrap/dist/css/bootstrap.css';
import { useState } from 'react';
import dataPartieDuDiscours from '../dataPartiesDeDiscours.js';
import DataQuestionPhrase from './QuestionAndPhrase.js';
import InputRadio from './InputRadio.js';
import Button from './Button.js';
// import useInscrement from '../hooks/useInCrement.js'


function ChangeQuestion({initialValue,maxValue, minValue }) {
    const [state, setState] = useState(initialValue)

    // const nextQuestion = () => {
    //     setState(state + 1)
    // }


    // return [
    //     state, nextQuestion
    // ]

    return {
        currentQuestion: state,
        increment: () => setState(v => v < maxValue ? v + 1 : v),
        decrement : () => setState(v => v > minValue ? v - 1: v)
    }

}


function DisplayQuiz() {

  const[currentQuestion,nextQuestion] = ChangeQuestion(0,dataPartieDuDiscours.length)

  return (
        <div className='container'>
        <DataQuestionPhrase
            question={dataPartieDuDiscours[currentQuestion].question}
            phrase={dataPartieDuDiscours[currentQuestion].phrase}
        />
        {dataPartieDuDiscours[currentQuestion].reponses.map((reponse,index) => (
             <div className="card-body">
             <label htmlFor={reponse.texte} key={index}>
              <InputRadio
                 type="radio"
                 id={index}
                 name="partieDuDiscours"
                 value={reponse.texte}
              />
              {reponse.texte}
            </label>
            </div>

        ))}
            <Button 
                type="button" 
                className="btn btn-dark"
                onClick={nextQuestion}
                >Next Question</Button>
{/* 
            <Button 
                type="button" 
                className="btn btn-dark"
                onClick={precedentQuestion}
                >Pr√©cedent Question</Button> */}
        </div>
  );
}

export default DisplayQuiz;
