import 'bootstrap/dist/css/bootstrap.css';
import dataPartieDuDiscours from './dataPartiesDeDiscours.js'
import ExercicePartieDuDiscours from './components/PartieDuDicours.js'
import Answers from './components/Answers.js'
import { Fragment } from 'react';
function App() {
  return (
    <Fragment>
      {dataPartieDuDiscours.map((quiz,index) => 
        <div key={index} className="container">
           <ExercicePartieDuDiscours 
            question={quiz.question}
            phrase={quiz.phrase}
           />
           {quiz.reponses.map((reponse,index)=> (
            <label htmlFor={index} key={index}>
              <Answers 
            id={index}
            checked = {checked}
            value={reponse}
            onChange={handleChangeValue}          
            />
            {reponse.texte}
            </label>


           ))
           }
        }
        </div>
      )}
      </Fragment>
  );
}

export default App;
