import 'bootstrap/dist/css/bootstrap.css';
import dataPartieDuDiscours from './dataPartiesDeDiscours.js'
import ExercicePartieDuDiscours from './components/PartieDuDicours.js'
import InputRadio from './components/InputRadio.js';
import { Fragment } from 'react';

function App() {
  return (
    <Fragment>
      {dataPartieDuDiscours.map((quiz,index) => 
        <div key={index} className="container">
           <ExercicePartieDuDiscours 
            question={quiz.question}
            phrase={quiz.phrase}
           />
           {quiz.reponses.map((reponse,index)=> (
             <div class="card-body">
            <label htmlFor={reponse.texte} key={index}>
              <InputRadio
                 type="radio"
                 id={index}
                 name="partieDuDiscours"
                 value={reponse.texte}
              />
              {reponse.texte}
            </label>
            </div>
           ))
           }
        </div>
      )}
      </Fragment>
  );
}

export default App;
