import 'bootstrap/dist/css/bootstrap.css';
import dataPartieDuDiscours from '../dataPartiesDeDiscours.js';
import DataQuestionPhrase from './QuestionAndPhrase.js';
import InputRadio from './InputRadio.js';
import { Fragment } from 'react';
import { useState } from 'react';

function handleNextButtonClick(initial = 0) {
    const [currentQuestion, setCurrentQuestion] = useState(initial);
    const nextQuestion = () => setCurrentQuestion(currentQuestion + 1);
    return [currentQuestion,nextQuestion]
}

function DisplayQuiz() {
  return (
    <Fragment>
      {dataPartieDuDiscours.map((quiz,index) => 
        <div key={index} className="container">
           <DataQuestionPhrase 
            question={quiz.question}
            phrase={quiz.phrase}
           />
           {quiz.reponses.map((reponse,index)=> (
             <div className="card-body">
             <label htmlFor={reponse.texte} key={index}>
              <InputRadio
                 type="radio"
                 id={index}
                 name="partieDuDiscours"
                 value={reponse.texte}
              />
              {reponse.texte}
            </label>
            </div>
           ))
           }
        </div>
      )}
      </Fragment>
  );
}

export default DisplayQuiz;
