import { Fragment } from "react"
import Answers from "./Answers.js";
import Button from "./Button.js";
const ExercicePartieDuDiscours = ({index,question, phrase,handleNextButtonClick,children}) => {

    return (
        <Fragment>
            <div className="card">
                <h5 class="card-title">{question}</h5>
                <p class="card-text">{phrase}</p>
                <Answers />
            </div>

            <Button 
                type="button"
                className="btn btn-primary"
                onClick = {handleNextButtonClick}
            >
                  {children}
            </Button>

            <Button 
                 type="button"
                 className="btn btn-success"
                 onClick = {handleNextButtonClick}>
                    {children}
                 </Button>

        </Fragment>
    )
}

export default ExercicePartieDuDiscours;
