import { useState } from "react"

export function useInscrement({initialValue = 0, max = Infinity, children}) {

    const [state,setState] = useState(initialValue)

    return {
        count: state,
        increment: () => setState(v => v < max ? v + 1 : {children}),
        decrement : () => setState(v => v - 1)

    }
}
